var app=angular.module("test-culqi",["ngRoute","ngPagination"]);app.config(["$routeProvider",function($routeProvider){$routeProvider.when("/",{templateUrl:"views/contenido.html",controller:"listadoController"}).when("/index",{templateUrl:"views/contenido.html",controller:"listadoController"}).when("/detalle/:_id",{templateUrl:"views/detalle.html",controller:"detalleController"}).otherwise({redirectTo:"/"})}]),app.controller("listadoController",function($scope,$http,$location){var url="https://raw.githubusercontent.com/jcontrerasm/data_sistema_ventas/master/data.json";$http.get(url).then(function(res){$scope.datos=res.data}),$scope.verDetalle=function(_id){$location.path("/detalle/"+_id)},$scope.mostrar_buscador=!1,$scope.mostrarbuscar=function(){0==$scope.mostrar_buscador?$scope.mostrar_buscador=!0:$scope.mostrar_buscador=!1}}),app.controller("detalleController",function($scope,$http,$routeParams,$location){var url="https://raw.githubusercontent.com/jcontrerasm/data_sistema_ventas/master/data.json";$http.get(url).then(function(res){var posicion=$routeParams._id;$scope.datos=res.data[posicion-1]}),$scope.regresar=function(){$location.path("/")}});
